"use client"

import { useState, useEffect } from "react"
import { useNavigate } from "react-router-dom"
import { useVoting } from "../context/VotingContext"
import LoadingSpinner from "../components/LoadingSpinner"
import { UserPlus, Wallet, AlertCircle, CheckCircle, XCircle } from "lucide-react"
import toast from "react-hot-toast"

function RegistrationPage() {
  const [formData, setFormData] = useState({
    voterId: "",
    name: "",
    nationalId: "",
    email: "",
  })
  const [isSubmitting, setIsSubmitting] = useState(false)
  const [registrationStatus, setRegistrationStatus] = useState(null)
  const [canRegister, setCanRegister] = useState(true)
  const [registrationMessage, setRegistrationMessage] = useState("")

  const { connectWallet, registerVoter, isConnected, account, voter, isAdmin, isLoading, contract } = useVoting()

  const navigate = useNavigate()

  useEffect(() => {
    if (isConnected && isAdmin) {
      navigate("/admin")
    } else if (isConnected && voter && voter.isVerified) {
      navigate("/dashboard")
    }
  }, [isConnected, isAdmin, voter, navigate])

  useEffect(() => {
    // Check if user already has a pending registration
    const checkRegistrationStatus = async () => {
      if (isConnected && contract && account) {
        try {
          const hasRequest = await contract.hasRegistrationRequest(account)
          if (hasRequest) {
            setRegistrationStatus("pending")
          }

          // Check if registration is allowed
          const registrationInfo = await contract.getRegistrationStatus()
          setCanRegister(registrationInfo.canRegister)
          setRegistrationMessage(registrationInfo.reason)
        } catch (error) {
          console.error("Error checking registration status:", error)
        }
      }
    }

    checkRegistrationStatus()

    // Set up interval to check registration status every 30 seconds
    const interval = setInterval(checkRegistrationStatus, 30000)
    return () => clearInterval(interval)
  }, [isConnected, contract, account])

  const handleInputChange = (e) => {
    const { name, value } = e.target
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }))
  }

  const handleSubmit = async (e) => {
    e.preventDefault()

    if (!isConnected) {
      toast.error("Please connect your wallet first")
      return
    }

    if (!canRegister) {
      toast.error("Voter registration is currently suspended")
      return
    }

    // Validate form
    if (!formData.voterId.trim() || !formData.name.trim() || !formData.nationalId.trim()) {
      toast.error("Please fill in all required fields")
      return
    }

    if (formData.email && !/\S+@\S+\.\S+/.test(formData.email)) {
      toast.error("Please enter a valid email address")
      return
    }

    setIsSubmitting(true)

    try {
      const success = await registerVoter(formData)
      if (success) {
        setRegistrationStatus("submitted")
        setFormData({
          voterId: "",
          name: "",
          nationalId: "",
          email: "",
        })
      }
    } catch (error) {
      console.error("Registration error:", error)
    } finally {
      setIsSubmitting(false)
    }
  }

  if (isLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <LoadingSpinner size="lg" text="Loading..." />
      </div>
    )
  }

  if (registrationStatus === "submitted" || registrationStatus === "pending") {
    return (
      <div className="min-h-screen flex items-center justify-center py-12 px-4 sm:px-6 lg:px-8">
        <div className="max-w-md w-full">
          <div className="bg-white shadow-xl rounded-lg p-8 text-center">
            <CheckCircle className="mx-auto h-16 w-16 text-green-500 mb-4" />
            <h2 className="text-2xl font-bold text-gray-900 mb-4">Registration Submitted</h2>
            <p className="text-gray-600 mb-6">
              Your registration request has been submitted successfully. Please wait for the Electoral Commission to
              verify your information.
            </p>
            <div className="bg-blue-50 border border-blue-200 rounded-md p-4 mb-6">
              <p className="text-sm text-blue-700">
                <strong>Next Steps:</strong>
                <br />
                1. The Electoral Commission will review your application
                <br />
                2. You'll be notified once verification is complete
                <br />
                3. Return to login once verified
              </p>
            </div>
            <button
              onClick={() => navigate("/login")}
              className="w-full px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 transition-colors"
            >
              Go to Login
            </button>
          </div>
        </div>
      </div>
    )
  }

  return (
    <div className="min-h-screen flex items-center justify-center py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-md w-full space-y-8">
        <div className="text-center">
          <UserPlus className="mx-auto h-12 w-12 text-blue-600" />
          <h2 className="mt-6 text-3xl font-extrabold text-gray-900">Voter Registration</h2>
          <p className="mt-2 text-sm text-gray-600">Register to participate in secure digital voting</p>
        </div>

        <div className="bg-white shadow-xl rounded-lg p-8 space-y-6">
          {/* Registration Status Alert */}
          {!canRegister && (
            <div className="bg-red-50 border border-red-200 rounded-md p-4">
              <div className="flex">
                <XCircle className="h-5 w-5 text-red-400 mr-2 flex-shrink-0" />
                <div className="text-sm text-red-700">
                  <p className="font-medium">Registration Suspended</p>
                  <p className="mt-1">{registrationMessage}</p>
                </div>
              </div>
            </div>
          )}

          {/* Wallet Connection */}
          <div className="space-y-4">
            <label className="block text-sm font-medium text-gray-700">Wallet Connection</label>

            {!isConnected ? (
              <button
                onClick={connectWallet}
                className="w-full flex items-center justify-center px-4 py-3 border border-gray-300 rounded-md shadow-sm bg-white text-sm font-medium text-gray-700 hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
              >
                <Wallet className="h-5 w-5 mr-2" />
                Connect MetaMask Wallet
              </button>
            ) : (
              <div className="flex items-center p-3 bg-green-50 border border-green-200 rounded-md">
                <div className="w-2 h-2 bg-green-500 rounded-full mr-3"></div>
                <div className="flex-1">
                  <p className="text-sm font-medium text-green-800">Wallet Connected</p>
                  <p className="text-xs text-green-600">
                    {account?.slice(0, 6)}...{account?.slice(-4)}
                  </p>
                </div>
              </div>
            )}
          </div>

          {/* Registration Form */}
          {isConnected && (
            <form onSubmit={handleSubmit} className="space-y-4">
              <div>
                <label htmlFor="voterId" className="block text-sm font-medium text-gray-700">
                  Voter ID *
                </label>
                <input
                  id="voterId"
                  name="voterId"
                  type="text"
                  required
                  disabled={!canRegister}
                  value={formData.voterId}
                  onChange={handleInputChange}
                  className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm placeholder-gray-400 focus:outline-none focus:ring-blue-500 focus:border-blue-500 disabled:bg-gray-100 disabled:cursor-not-allowed"
                  placeholder="Enter your Voter ID"
                />
              </div>

              <div>
                <label htmlFor="name" className="block text-sm font-medium text-gray-700">
                  Full Name *
                </label>
                <input
                  id="name"
                  name="name"
                  type="text"
                  required
                  disabled={!canRegister}
                  value={formData.name}
                  onChange={handleInputChange}
                  className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm placeholder-gray-400 focus:outline-none focus:ring-blue-500 focus:border-blue-500 disabled:bg-gray-100 disabled:cursor-not-allowed"
                  placeholder="Enter your full name"
                />
              </div>

              <div>
                <label htmlFor="nationalId" className="block text-sm font-medium text-gray-700">
                  National ID *
                </label>
                <input
                  id="nationalId"
                  name="nationalId"
                  type="text"
                  required
                  disabled={!canRegister}
                  value={formData.nationalId}
                  onChange={handleInputChange}
                  className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm placeholder-gray-400 focus:outline-none focus:ring-blue-500 focus:border-blue-500 disabled:bg-gray-100 disabled:cursor-not-allowed"
                  placeholder="Enter your National ID"
                />
              </div>

              <div>
                <label htmlFor="email" className="block text-sm font-medium text-gray-700">
                  Email Address
                </label>
                <input
                  id="email"
                  name="email"
                  type="email"
                  disabled={!canRegister}
                  value={formData.email}
                  onChange={handleInputChange}
                  className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm placeholder-gray-400 focus:outline-none focus:ring-blue-500 focus:border-blue-500 disabled:bg-gray-100 disabled:cursor-not-allowed"
                  placeholder="Enter your email (optional)"
                />
              </div>

              <button
                type="submit"
                disabled={isSubmitting || !canRegister}
                className="w-full flex justify-center py-3 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 disabled:opacity-50 disabled:cursor-not-allowed"
              >
                {isSubmitting ? <LoadingSpinner size="sm" text="" /> : "Submit Registration"}
              </button>

              {!canRegister && (
                <p className="text-sm text-red-600 text-center">
                  Registration will resume after the current election ends.
                </p>
              )}
            </form>
          )}

          {/* Login Link */}
          <div className="text-center pt-4 border-t border-gray-200">
            <p className="text-sm text-gray-600">
              Already registered?{" "}
              <button onClick={() => navigate("/login")} className="font-medium text-blue-600 hover:text-blue-500">
                Login here
              </button>
            </p>
          </div>

          {/* Important Notice */}
          <div className="bg-yellow-50 border border-yellow-200 rounded-md p-4">
            <div className="flex">
              <AlertCircle className="h-5 w-5 text-yellow-400 mr-2 flex-shrink-0" />
              <div className="text-sm text-yellow-700">
                <p className="font-medium">Important Notice</p>
                <p className="mt-1">
                  {canRegister
                    ? "Your registration will be reviewed by the Electoral Commission. Make sure all information is accurate and matches your official documents."
                    : "Voter registration is suspended during active elections to ensure election integrity. Please try again after the current election ends."}
                </p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  )
}

export default RegistrationPage
